<?php
/**
 * SVG Icons class
 *
 * @package Azeria
 * @since 1.2.0
 */

/**
 * This class is in charge of displaying SVG icons across the site.
 *
 * Place each <svg> source on its own array key, without adding the
 * both `width` and `height` attributes, since these are added dynamically,
 * before rendering the SVG code.
 *
 * All icons are assumed to have equal width and height, hence the option
 * to only specify a `$size` parameter in the svg methods.
 *
 * @since 1.2.0
 */
class Azeria_SVG_Icons {

	/**
	 * Gets the SVG code for a given icon.
	 *
	 * @since  1.2.0
	 * @param  string $type          Icon type.
	 * @param  string $icon          Icon name.
	 * @param  array|string $classes Additional CSS classes
	 * @return null|string
	 */
	public static function get_svg( $type = 'ui', $icon = '', $classes = array() ) {

		switch ( $type ) {
			case 'ui':
				$arr = apply_filters( 'azeria_svg_ui_icons', self::$ui_icons );
				break;

			case 'social':
				$arr = apply_filters( 'azeria_svg_social_icons', self::$social_icons );
				break;

			default:
				$arr = array();
		}

		$classes   = (array) $classes;
		$classes[] = 'svg-icon';

		if ( array_key_exists( $icon, $arr ) ) {
			$repl = '<svg aria-hidden="true" role="img" focusable="false" width="32" height="32" ';
			$svg  = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) ); // Add extra attributes to SVG code.
			$svg  = preg_replace( "/([\n\t]+)/", ' ', $svg ); // Remove newlines & tabs.
			$svg  = preg_replace( '/>\s*</', '><', $svg ); // Remove white space between SVG tags.

			return sprintf( '<i class="%1$s">%2$s</i>', join( ' ', $classes ), $svg );
		}

		return null;
	}

