/**
 * Gets the SVG code for a given icon.
 *
 * @since  1.2.0
 * @param  string $icon          Icon name.
 * @param  array|string $classes Additional CSS classes
 * @return null|string
 */
function azeria_get_icon_svg( $icon = '', $classes = array() ) {
	return Azeria_SVG_Icons::get_svg( 'ui', $icon, $classes );
}

/**
 * Gets the SVG code for a given icon.
 *
 * @since  1.2.0
 * @param  string $icon          Icon name.
 * @param  array|string $classes Additional CSS classes
 * @return null|string
 */
function azeria_get_social_icon_svg( $icon = '', $classes = array() ) {
	return Azeria_SVG_Icons::get_svg( 'social', $icon, $classes );
}
